const mysql = require('mysql');

export default connection = mysql.createConnection({
    host: 'localhost',
    user: 'sserver',
    password: 'sserver',
    database: 'tia_asa'
});
connection.connect((err) => {
	if (err) throw err;
	alert("Connected!");
});
