const mysql = require('mysql');

const connection = mysql.createConnection({
    host: 'localhost',
    user: 'sserver',
    password: 'sserver',
    database: 'tia_asa'
});
connection.connect((err) => {
	if (err) throw err;
	alert("Connected!");
});

export default connection;
